import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";

import "../styles/tour.css";
import TourCard from "../shared/TourCard";
import SearchBar from "../shared/SearchBar";
import tourData from "../assets/data/tours";
import { Container } from "reactstrap";
import { Row, Col } from "reactstrap";
import { Link } from "react-router-dom";

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  useEffect(() => {
    const pages = Math.ceil(5 / 4);
    setPageCount(pages);
  }, [page]);

  return (
    <>
      <CommonSection />

      <section>
        <Container>
          <Row>
            <h2 className="tourtitle"> Tour Guide</h2>
            <div style={{ background: "grey", height: "1px" }}></div>
            <Col lg="6">
              <h2>Pilih Tour Guide yang kamu inginkan!</h2>
              <SearchBar />
            </Col>
            <Col lg="6">
              <h2>Apakah kamu ingin mendaftarkan Tour Guide?</h2>
              <Link>Daftar menjadi Tour Guide</Link>
            </Col>
          </Row>
        </Container>
      </section>
      <section>
        <Container>
          <Row>
            {tourData?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}

            <Col>
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3"></div>
              {[...Array(pageCount).keys()].map((number) => (
                <span key={number} onClick={number}>
                  {number + 1}
                </span>
              ))}
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Tours;
